    def eden_icon(name, size: :md, **html_options)
      name = name.to_s.tr("_", "-")
      svg_content = ICONS[name]
      return "".html_safe unless svg_content

      size_class = ICON_SIZES[size] || ICON_SIZES[:md]
      css_class = token_list(size_class, html_options.delete(:class))

      attrs = {
        xmlns: "http://www.w3.org/2000/svg",
        fill: "none",
        viewBox: "0 0 24 24",
        "stroke-width": "1.5",
        stroke: "currentColor",
        class: css_class,
        "aria-hidden": "true"
      }.merge(html_options)

      attr_string = attrs.map { |k, v| %(#{k}="#{ERB::Util.html_escape(v)}") }.join(" ")

      "<svg #{attr_string}>#{svg_content}</svg>".html_safe
    end
  end
